INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303014101', 'מי היה וינסנט ואן גוך?', 'וינסנט ואן גוך היה צייר מפורסם שיצר בין היתר את "ליל כוכבים", "החמניות" ואת "אוכלי תפוחי האדמה".');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303014102', 'מהו פסל?', 'פסל הוא יצירת אמנות תלת-ממדית שניתן לפסל מחומרים שונים.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303014103', 'איזה צבע מתקבל מערבוב של כחול ואדום?', 'כאשר מערבבים צבע כחול ואדום מתקבל הצבע הסגול.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303014104', 'באיזו טכניקה צייר לאונרדו דה וינצ`י את "המונה ליזה"?', '"המונה ליזה" היא יצירת אומנות הידועה גם בשם "לה ג`וקונדה".');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303014105', 'מהו מוזיאון?', 'מוזיאון הוא מקום שבו מוצגות יצירות אמנות וממצאים היסטוריים.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303015101', 'באיזו מדינה דרקונים ופנסי תאורה מנייר הם חלק מובנה בתרבות?', 'לוח השנה הסיני שונה מלוח השנה הנהוג במערב.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303015102', 'מהי השפה המדוברת ביותר במקסיקו?', 'למקסיקו אין שפה רשמית אך השפה המדוברת ביותר היא ספרדית.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303015103', 'איזו מדינה ידועה בקרנבלים הססגוניים שלה?', 'הקרנבל הברזילאי נחגג במשך ארבעה ימים. הקרנבל מתחיל בכל שנה חמישים יום לפני חג הפסחא.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303015104', 'באיזו מדינה נמצא הטאג` מהל?', 'הטאג` מהל נחשב לאחד מפלאי העולם.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303015105', 'באיזו מדינה סושי הוא מאכל פופולארי במיוחד?', 'יפן ידועה במנות הסושי המסורתיות שלה.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303016101', 'מיהו מחבר הספר "הנסיך הקטן"?', '"הנסיך הקטן" נכתב על ידי אנטואן דה סן-אכזופרי והוא אחד מהספרים הנקראים ביותר בעולם.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303016102', 'איזה צמד אחים פרסם מעשיות רבות?', '"האחים גרים" הם: יאקוב גרים (1785–1863) ווילהלם גרים (1786–1859).');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303016103', 'עלילת איזה ספר עוסקת בבובת עץ שהפכה לילד אמיתי?', 'פינוקיו הוא הדמות המרכזית בסיפור "הרפתקאותיו של פינוקיו" מאת קרלו קולודי.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303016104', 'איזה בעל חיים מוזכר במגילת אסתר?', 'מרדכי רכב על סוס מלכותי כאשר המן הלך לפניו וקרא: "ככה יעשה לאיש אשר המלך חפץ ביקרו".');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303016105', 'באיזה סיפור נסיכה נדקרת ממחט לאחר שהוטלה עליה קללה?', '"היפיפיה הנרדמת" הוא סיפור עם שבו הנסיכה מתעוררת לבסוף בזכות נשיקת נסיך.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303017101', 'איזו יבשת היא היבשת הגדולה ביותר?', 'אסיה היא היבשת הגדולה ביותר בעולם ובה כמחצית מאוכלוסיית העולם.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303017102', 'מהו האוקיינוס הכי גדול?', 'האוקיינוס השקט הוא הגדול ביותר וכולל כשליש משטח פני כדור הארץ.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303017103', 'מהו קו הרוחב המכונה קו המשווה?', 'קו המשווה חוצה את כדור הארץ לשני חלקים שווים.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303017104', 'מהו ההר הגבוה בעולם?', 'האוורסט בהימלאיה הוא ההר הגבוה בעולם. גובהו כ-8,850 מטרים.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303017105', 'לאיזו ממדינות העולם השטח הגדול ביותר?', 'רוסיה היא המדינה הגדולה ביותר בעולם. שטחה  כ-17 מיליון קמ"ר.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303018101', 'איזה בעל חיים נמצא כיום בסכנת הכחדה?', 'הטיגריס הסיבירי נמצא בסכנת הכחדה עקב ציד בלתי חוקי והרס בית גידול.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303018102', 'מה שמו של בעל החיים שבתמונה?', 'קרנפים מסוגלים לרוץ מהר מאוד למרחקים קצרים.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303018103', 'איזו חיה מוזכרת בשיר ילדים מפורסם מאת לוין קיפניס?', 'מי יודע מדוע ולמה, לובשת הזברה פיז`מה?');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303018104', 'על פי המסופר בתנ"ך היכן שהה הנביא יונה שלושה ימים? ', 'לאחר שלושה ימים בתוך הדג יונה הזהיר את אנשי נינווה מפני חורבן');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303018105', 'לאיזו ציפור מקור צהוב?', 'המיינות בישראל הנן מין פולש');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303019101', 'לאיזה חומר מקדם שבירה גבוה שגורם לקרני האור להישבר בתוכו פעמים רבות?', 'היהלום הוא האבן הקשה ביותר בטבע. הוא נוצר מפחמן בלחץ גבוה.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303019102', 'לאיזו אבן חן צבע ירקרק?', 'הברקת מוזכרת בתנ"ך כאחת מאבני החושן.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303019103', 'מה ניתן לומר על אבן גיר?', 'אבן גיר היא סלע משקע המורכבת מהמינרל קלציט (סידן פחמתי). המקור העיקרי של סידן פחמתי הוא בשלדי בעלי חיים ימיים.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303019104', 'מה צבעה של אבן ספיר?', 'ספיר מוזכר בתנ"ך כאחד מאבני החושן');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303019105', 'מהו הגביש השקוף שנהוג להשתמש בו בייצור שעונים?', 'טמפרטורת ההתכה של הקוורץ היא 1700 מעלות צלזיוס.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303020101', 'מהו תחום המומחיות של נפרולוג?', 'נפרולוגיה עוסקת בתפקוד הכליות.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303020102', 'איזה בעל חיים לא נמצא בטבע בישראל?', 'שפיפון הוא נחש ארסי. הגרביל הוא מכרסם קופצני והתן שייך למשפחת הכלביים.');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303020103', 'בערבית "מועלם" ובעברית...', 'ספר הפילוסופיה החשוב של הרמב"ם נקרא "מורה נבוכים".');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303020104', 'מה היה המקצוע של דוד גרוסמן, עמוס עוז ואשר קרביץ?', 'ספרות עברית התקיימה מימי התנ"ך ועד היום');
INSERT INTO questions (topic_id, c_grade_id, level,question_id,interesting_fact , question_text) VALUES (1,3,1,'303020105', 'כיצד מכונה המקצוע של מתכנני בתים ובניינים?', 'אדריכל מתכנן ומעצב מבנים.');
